        foreach ($barang as $row) {
            $X += 1;
            $pdf->Cell(10, 6, $X, 1, 0, 'C');
            $pdf->Cell(30, 6, $row->barang, 1, 0);
            $pdf->Cell(25, 6, $row->nm_ruangan, 1, 0);
            $pdf->Cell(95, 6, substr($row->ket_barang, 0, 53), 1, 0, 'L');
            $pdf->Cell(25, 6, date("d-M-Y", strtotime($row->tgl_kehilangan)), 1, 1, 'C');
        }

        $pdf->SetFont('Arial', '', 10);
        $pdf->Ln();
        $pdf->Cell(185, 6, 'Tegal, ' . date("d-M-Y"), 0, 1, 'R');
        $pdf->Ln();
        $pdf->Cell(95, 7, 'Petugas,', 0, 0, 'C');
        $pdf->Cell(95, 7, 'Penerima,', 0, 1, 'C');
        $pdf->Ln();
        $petugas = $this->session->userdata("nama");
        $pdf->Cell(95, 7, '( ' . $petugas . ' )', 0, 0, 'C');
        $pdf->Cell(95, 7, '(............................................)', 0, 1, 'C');
        $pdf->Output();
    }

    /*
     * Editing a kehilangan
     */
    function edit($id_kehilangan)
    {
        // check if the kehilangan exists before trying to edit it
        $data['kehilangan'] = $this->Kehilangan_model->get_kehilangan($id_kehilangan);

        if (isset($data['kehilangan']['id_kehilangan'])) {
            if (isset($_POST) && count($_POST) > 0) {
                $params = array(
                    'barang' => $this->input->post('barang'),
                    'lokasi' => $this->input->post('lokasi'),
                    'ket_barang' => $this->input->post('ket_barang'),
                    'tgl_kehilangan' => $this->input->post('tgl_kehilangan'),
                    'penemu' => $this->input->post('penemu'),
                    'status' => 'belum',
                );

                $this->Kehilangan_model->update_kehilangan($id_kehilangan, $params);
                redirect('kehilangan/index');
            } else {
                $data['ruangan'] = $this->Kehilangan_model->get_all_ruangan();
                $data['_view'] = 'kehilangan/edit';
                if ($this->session->userdata("level") == 'admin') {
                $this->load->view('layouts/main', $data);
            } else {
                $this->load->view('layouts/main_user', $data);
            }
            }
        } else
            show_error('The kehilangan you are trying to edit does not exist.');
    }

    function ambil($id_kehilangan)
    {
        // check if the kehilangan exists before trying to edit it
        $data['kehilangan'] = $this->Kehilangan_model->get_kehilangan($id_kehilangan);

        if (isset($data['kehilangan']['id_kehilangan'])) {
            if (isset($_POST) && count($_POST) > 0) {
                $params = array(
                    'barang' => $this->input->post('barang'),
                    'lokasi' => $this->input->post('lokasi'),
                    'ket_barang' => $this->input->post('ket_barang'),
                    'tgl_kehilangan' => $this->input->post('tgl_kehilangan'),
                    'penemu' => $this->input->post('penemu'),
                    'status' => 'ambil',
                );

                $this->Kehilangan_model->ambil_kehilangan($id_kehilangan, $params);
                redirect('kehilangan/index');
            } else {
                $data['ruangan'] = $this->Kehilangan_model->get_all_ruangan();
                $data['_view'] = 'kehilangan/ambil';
                if ($this->session->userdata("level") == 'admin') {
                $this->load->view('layouts/main', $data);
            } else {
                $this->load->view('layouts/main_user', $data);
            }
            }
        } else
            show_error('The kehilangan you are trying to edit does not exist.');
    }
    /*
     * Deleting kehilangan
     */
    function remove($id_kehilangan)
    {
        $kehilangan = $this->Kehilangan_model->get_kehilangan($id_kehilangan);

        // check if the kehilangan exists before trying to delete it
        if (isset($kehilangan['id_kehilangan'])) {
            $this->Kehilangan_model->delete_kehilangan($id_kehilangan);
            redirect('kehilangan/index');
        } else
            show_error('The kehilangan you are trying to delete does not exist.');
    }
