class Barang_hp_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }

    function get_barang($id_barang)
    {
        return $this->db->get_where('barang_hp', array('id_barang' => $id_barang))->row_array();
    }
    function data($number, $offset)
    {
        return $query = $this->db->get('barang_hp', $number, $offset)->result();
    }

    function jumlah_data()
    {
        return $this->db->get('barang_hp')->num_rows();
    }

    function jumlah_data_by($keyword)
    {
        $this->db->select('*');
        $this->db->from('barang_hp a');
        $this->db->join('kategori b', 'b.id_kategori=a.id_kategori', 'left');
        $this->db->join('merk c', 'c.id_merk=a.id_merk', 'left');
        $this->db->join('satuan d', 'd.id_satuan=a.id_satuan', 'left');
        $this->db->like('a.nm_barang', $keyword);
        $this->db->order_by('a.id_barang', 'asc');
        $query = $this->db->get();
        return $query->num_rows();
    }
    /*
     * Get all barang
     */
    function get_all_barang($number, $offset)
    {
        $this->db->select('*');
        $this->db->from('barang_hp a');
        $this->db->join('kategori b', 'b.id_kategori=a.id_kategori', 'left');
        $this->db->join('merk c', 'c.id_merk=a.id_merk', 'left');
        $this->db->join('satuan d', 'd.id_satuan=a.id_satuan', 'left');
        //$this->db->where('c.album_id',$id);
        $this->db->order_by('a.id_barang', 'asc');
        $this->db->limit($number, $offset);
        $query = $this->db->get();
        return $query->result_array();

        //    $this->db->order_by('id_barang', 'desc');
        //    return $this->db->get('barang')->result_array();
    }

    function search($number, $offset, $keyword)
    {
        $this->db->select('*');
        $this->db->from('barang_hp a');
        $this->db->join('kategori b', 'b.id_kategori=a.id_kategori', 'left');
        $this->db->join('merk c', 'c.id_merk=a.id_merk', 'left');
        $this->db->join('satuan d', 'd.id_satuan=a.id_satuan', 'left');
        $this->db->like('a.nm_barang', $keyword);
        $this->db->order_by('a.id_barang', 'asc');
        $this->db->limit($number, $offset);
        $query = $this->db->get();
        return $query->result_array();

        //    $this->db->order_by('id_barang', 'desc');
        //    return $this->db->get('barang')->result_array();
    }
    /*
     * function to add new barang
     */
    function add_barang($params)
    {
        $this->db->insert('barang_hp', $params);
        return $this->db->insert_id();
    }

    function get_all_kategori()
    {
        $this->db->order_by('nm_kategori', 'asc');
        return $this->db->get('kategori')->result_array();
    }

    function get_all_merk()
    {
