    }

    function get_all_satuan()
    {
        $this->db->order_by('nm_satuan', 'asc');
        return $this->db->get('satuan')->result_array();
    }

    function get_satuan($id)
    {
        $this->db->select('*');
        $this->db->from('barang_hp');
        $this->db->where('id_satuan', $id);
        $this->db->order_by('nm_barang', 'asc');
        $query = $this->db->get();
        return $query->result_array();
    }

