    /*
     * function to add new inventaris
     */
    function add_inventaris($params)
    {
        $this->db->insert('inventaris', $params);
        return $this->db->insert_id();
    }

    function get_all_barang()
    {
        $this->db->order_by('nm_barang', 'asc');
        return $this->db->get('barang')->result_array();
    }

    function get_all_ruangan()
    {
        $this->db->order_by('nm_ruangan', 'asc');
        return $this->db->get('ruangan')->result_array();
    }

    /*
     * function to update inventaris
     */
    function update_inventaris($id_inventaris, $params)
    {
        $this->db->where('id_inventaris', $id_inventaris);
        return $this->db->update('inventaris', $params);
    }

    /*
     * function to delete inventaris
     */
    function delete_inventaris($id_inventaris)
    {
        return $this->db->delete('inventaris', array('id_inventaris' => $id_inventaris));
    }
